<script>
export default {
  watch: {
    //   $route() {
    //     $("#navbarCollapse").collapse("hide");
    //   },
  },
  data() {
    return {
      onLine: navigator.onLine,
      searchData: "",
      baseUrl: window.location.origin,
      csrf: document
          .querySelector('meta[name="csrf-token"]')
          .getAttribute("content"),
      adminLogin: document
          .querySelector('meta[name="admin-login"]')
          .getAttribute("content"),
      tasksCount: 0,
      buildings: [],
      selectedBuilding: "",
      showBuildingMenu: false,
      showMenu: false,
      permissions: [],
    };
  },
  methods: {
    updateGateUser(data) {
      this.$gate.user = data;
      this.$forceUpdate();
    },
    search() {
      this.$router.push("/search?q=" + this.searchData).catch((err) => {
      });
    },
    clearBuildingInSession(redirect = false) {
      const params = {
        building_id: this.selectedBuilding,
      };
      this.$http
          .post("/clearBuildingInSession", params)
          .then((response) => {
            this.selectedBuilding = 0;
            this.showBuildingMenu = false;
            this.showMenu = false;
            this.$store.dispatch("setBuildingId", null);
            this.$gate.building_id = null;
            if (redirect) {
              this.$router.push(redirect);
            }
          })
          .catch((error) => {
            console.error(error);
          });
    },
    setBuildingInSession(buildingId = 0) {
      console.log(buildingId, "BUILDING ID");
      let alreadyInBuilding = this.showBuildingMenu;
      
      if (this.isNumber(buildingId) && buildingId > 0)
        this.selectedBuilding = buildingId;
      const params = {
        building_id: this.selectedBuilding,
      };
      this.$http
          .post("/setBuildingIdInSession", params)
          .then((response) => {
            this.showBuildingMenu = true;
            this.showMenu = true;
            console.log("menuu", this.showBuildingMenu);
            this.$gate.user.selectedBuilding = response.data.building;
            this.$store.dispatch("setBuildingId", this.selectedBuilding);
            this.$gate.building_id = this.selectedBuilding;
            // if (!alreadyInBuilding) this.$router.push("/");

            if (this.isNumber(buildingId) && buildingId > 0)
              this.selectedBuilding = buildingId;
          })
          .catch((error) => {
            console.error(error);
          });
    },
    getBuildings() {
      this.$http
          .get("/buildings")
          .then((response) => {
            this.buildings = response.data.data;
          })
          .catch((error) => {
            console.error(error);
          });
    },
    setAmazonUrl() {
      this.$http
          .get("/loadAmazonUrl")
          .then((response) => {
            let amazonUrl = response.data + "musentry/";
            $("#mus_entry_url").val(amazonUrl);
          })
          .catch((error) => {
            console.error(error);
          });
    },
    debouncedSearch() {
      clearTimeout(this.debounceTimeout);
      this.debounceTimeout = setTimeout((e) => this.search(), 2000);
    },
    logout() {
      event.preventDefault();
      $(".logout-form").submit();
    },
    getTasksCount() {
      this.$http
          .get("/tasks/count")
          .then((response) => {
            response = response.data;
            this.tasksCount = response.count;
          })
          .catch((error) => {
            console.error(error);
          });
    },
    goToMainMenu() {
      this.showBuildingMenu = false;
    },
    openNav() {
      this.showMenu = true;
    },
    closeNav() {
      this.showMenu = false;
    },
  },
  created() {
    console.log(this.$gate, "GATE");
    this.getTasksCount();
    this.getBuildings();
    this.setAmazonUrl();
  },
  mounted() {
    let self = this;
    if (this.$gate.building_id > 0) this.showBuildingMenu = true;
    this.showMenu = true;
    this.selectedBuilding = this.$gate.building_id;
    this.$store.dispatch("setBuildingId", this.selectedBuilding);

    var tooltipTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="tooltip"]'))
    var tooltipList = tooltipTriggerList.map(function (tooltipTriggerEl) {
      return new bootstrap.Tooltip(tooltipTriggerEl)
    })
  },
  computed: {
    currentRouteName() {
      if (this.$route.name === "Staff Detail") {
        return "staff";
      } else {
        if (this.$route.name === 'Dashboard') {
          if (this.$gate.building_id > 0) return 'building';
          else return 'portfolio';
        }
        return this.$route.name;
      }
    },
  },
};
